sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	if(current.tablename =='incident') {
		var  attRec = new GlideRecord('sys_attachment');
		attRec.addQuery('table_sys_id', current.table_sys_id);
		attRec.query();
		while(attRec.next()) {
			var getCount = attRec.getRowCount();
			if(getCount > 3) {
				gs.addErrorMessage('Maximum number of attachments allowed are 3'); 
				current.setAbortAction(true);
			}
		}
	}
})(current, previous);
